import {
  View,
  Text,
  Pressable,
  ActivityIndicator,
  ScrollView,
  StyleSheet,
  Modal,
  Alert,
} from "react-native";
import { useEffect, useMemo, useState } from "react";
import { colors } from "@/constants/color";
import { useRouter } from "expo-router";
import { collection, onSnapshot, query, where, addDoc } from "firebase/firestore";
import { db, auth } from "@/lib/firebase";
import { LinearGradient } from "expo-linear-gradient";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import {
  Coffee,
  Moon,
  Apple,
  Droplet,
  FileText,
  Camera,
  Users,
  Calendar,
  Plus,
  ChevronDown,
  X,
  Check,
  Clock,
  Baby,
  Heart,
  Activity,
} from "lucide-react-native";

type Child = {
  id: string;
  name: string;
  classroomId?: string;
  status?: string;
};

type EntryType = {
  id: string;
  label: string;
  icon: any;
  color: string;
  bgColor: string;
  subtypes?: string[];
};

const entryTypes: EntryType[] = [
  {
    id: "attendance",
    label: "Attendance",
    icon: Clock,
    color: "#10B981",
    bgColor: "#D1FAE5",
    subtypes: ["Check In", "Check Out"],
  },
  {
    id: "meal",
    label: "Meal",
    icon: Apple,
    color: "#F59E0B",
    bgColor: "#FEF3C7",
    subtypes: ["Breakfast", "Lunch", "Snack"],
  },
  {
    id: "nap",
    label: "Nap Time",
    icon: Moon,
    color: "#6366F1",
    bgColor: "#E0E7FF",
    subtypes: ["Started", "Woke Up"],
  },
  {
    id: "diaper",
    label: "Diaper",
    icon: Baby,
    color: "#EC4899",
    bgColor: "#FCE7F3",
    subtypes: ["Wet", "BM", "Dry"],
  },
  {
    id: "activity",
    label: "Activity",
    icon: Activity,
    color: "#8B5CF6",
    bgColor: "#EDE9FE",
    subtypes: ["Outdoor Play", "Art & Craft", "Story Time", "Music"],
  },
  {
    id: "photo",
    label: "Photo",
    icon: Camera,
    color: "#06B6D4",
    bgColor: "#CFFAFE",
  },
  {
    id: "note",
    label: "Note",
    icon: FileText,
    color: "#64748B",
    bgColor: "#F1F5F9",
  },
  {
    id: "health",
    label: "Health",
    icon: Heart,
    color: "#EF4444",
    bgColor: "#FEE2E2",
    subtypes: ["Temperature", "Medicine", "Incident"],
  },
];

export default function TeacherDashboard() {
  const insets = useSafeAreaInsets();
  const [children, setChildren] = useState<Child[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedClass, setSelectedClass] = useState<string>("all");
  const [showClassPicker, setShowClassPicker] = useState(false);
  const [selectedChildren, setSelectedChildren] = useState<string[]>([]);
  const [showChildPicker, setShowChildPicker] = useState(false);
  const router = useRouter();

  // Live subscription to children
  useEffect(() => {
    const q = query(collection(db, "children"), where("status", "==", "enrolled"));
    const unsub = onSnapshot(
      q,
      (snap) => {
        const rows: Child[] = snap.docs.map((d) => {
          const x = d.data() as any;
          return {
            id: d.id,
            name: x.name ?? "(no name)",
            classroomId: x.classroomId,
            status: x.status,
          };
        });
        rows.sort((a, b) => a.name.localeCompare(b.name));
        setChildren(rows);
        setLoading(false);
      },
      () => {
        setChildren([]);
        setLoading(false);
      }
    );
    return () => unsub();
  }, []);

  // Get unique classrooms
  const classrooms = useMemo(() => {
    const unique = new Set<string>();
    children.forEach((c) => {
      if (c.classroomId) unique.add(c.classroomId);
    });
    return Array.from(unique).sort();
  }, [children]);

  // Filter children by selected class
  const filteredChildren = useMemo(() => {
    if (selectedClass === "all") return children;
    return children.filter((c) => c.classroomId === selectedClass);
  }, [children, selectedClass]);

  const handleEntryTypePress = async (type: EntryType) => {
    if (selectedChildren.length === 0) {
      Alert.alert("Select Children", "Please select at least one child first");
      return;
    }

    // For now, just show what would be created
    Alert.alert(
      `Create ${type.label} Entry`,
      `Would create ${type.label} entry for ${selectedChildren.length} children`,
      [
        { text: "Cancel", style: "cancel" },
        {
          text: "Create",
          onPress: async () => {
            // Here you would navigate to a detailed entry form
            // or create the entry directly
            console.log("Creating entry:", type.id, selectedChildren);
          },
        },
      ]
    );
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={colors.tint} />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Gradient Background */}
      <LinearGradient
        colors={["#E0E7FF", "#F0F4FF", "#FFFFFF"]}
        style={styles.gradientBackground}
      />

      <ScrollView
        style={styles.scrollView}
        contentContainerStyle={{ paddingBottom: 100 }}
        showsVerticalScrollIndicator={false}
      >
        {/* Header Section */}
        <View style={[styles.header, { paddingTop: insets.top + 20 }]}>
          <Text style={styles.greeting}>Good Morning! ðŸ‘‹</Text>
          <Text style={styles.title}>Create Entry</Text>
        </View>

        {/* Class & Children Selection */}
        <View style={styles.selectionContainer}>
          {/* Class Selector */}
          <Pressable
            style={styles.selector}
            onPress={() => setShowClassPicker(true)}
          >
            <View style={styles.selectorIcon}>
              <Users size={20} color="#6366F1" />
            </View>
            <View style={styles.selectorContent}>
              <Text style={styles.selectorLabel}>Class</Text>
              <Text style={styles.selectorValue}>
                {selectedClass === "all" ? "All Classes" : `Class ${selectedClass}`}
              </Text>
            </View>
            <ChevronDown size={20} color="#94A3B8" />
          </Pressable>

          {/* Children Count */}
          <Pressable
            style={[styles.selector, styles.childSelector]}
            onPress={() => setShowChildPicker(true)}
          >
            <View style={[styles.selectorIcon, { backgroundColor: "#FCE7F3" }]}>
              <Baby size={20} color="#EC4899" />
            </View>
            <View style={styles.selectorContent}>
              <Text style={styles.selectorLabel}>Children</Text>
              <Text style={styles.selectorValue}>
                {selectedChildren.length === 0
                  ? "Select"
                  : selectedChildren.length === filteredChildren.length
                  ? "All Selected"
                  : `${selectedChildren.length} Selected`}
              </Text>
            </View>
            <ChevronDown size={20} color="#94A3B8" />
          </Pressable>
        </View>

        {/* Selected Children Pills */}
        {selectedChildren.length > 0 && (
          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={styles.selectedChildrenContainer}
          >
            {selectedChildren.map((childId) => {
              const child = children.find((c) => c.id === childId);
              return (
                <View key={childId} style={styles.selectedChildPill}>
                  <Text style={styles.selectedChildName}>{child?.name}</Text>
                  <Pressable
                    onPress={() =>
                      setSelectedChildren(selectedChildren.filter((id) => id !== childId))
                    }
                  >
                    <X size={14} color="#6366F1" />
                  </Pressable>
                </View>
              );
            })}
          </ScrollView>
        )}

        {/* Entry Types Grid */}
        <View style={styles.entryTypesContainer}>
          <Text style={styles.sectionTitle}>What would you like to record?</Text>
          <View style={styles.entryGrid}>
            {entryTypes.map((type) => (
              <Pressable
                key={type.id}
                style={({ pressed }) => [
                  styles.entryCard,
                  { transform: [{ scale: pressed ? 0.95 : 1 }] },
                ]}
                onPress={() => handleEntryTypePress(type)}
              >
                <View style={[styles.entryIconContainer, { backgroundColor: type.bgColor }]}>
                  <type.icon size={24} color={type.color} strokeWidth={2} />
                </View>
                <Text style={styles.entryLabel}>{type.label}</Text>
                {type.subtypes && (
                  <Text style={styles.entrySubtypes}>{type.subtypes.length} options</Text>
                )}
              </Pressable>
            ))}
          </View>
        </View>

        {/* Quick Actions */}
        <View style={styles.quickActionsContainer}>
          <Text style={styles.sectionTitle}>Quick Actions</Text>
          <Pressable style={styles.quickAction}>
            <View style={styles.quickActionIcon}>
              <Calendar size={20} color="#10B981" />
            </View>
            <Text style={styles.quickActionText}>Today's Schedule</Text>
            <ChevronDown size={18} color="#94A3B8" style={{ transform: [{ rotate: '-90deg' }] }} />
          </Pressable>
          <Pressable style={styles.quickAction}>
            <View style={styles.quickActionIcon}>
              <FileText size={20} color="#F59E0B" />
            </View>
            <Text style={styles.quickActionText}>View Reports</Text>
            <ChevronDown size={18} color="#94A3B8" style={{ transform: [{ rotate: '-90deg' }] }} />
          </Pressable>
        </View>
      </ScrollView>

      {/* Class Picker Modal */}
      <Modal visible={showClassPicker} transparent animationType="slide">
        <Pressable style={styles.modalOverlay} onPress={() => setShowClassPicker(false)}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Select Class</Text>
            <Pressable
              style={styles.modalOption}
              onPress={() => {
                setSelectedClass("all");
                setShowClassPicker(false);
              }}
            >
              <Text style={styles.modalOptionText}>All Classes</Text>
              {selectedClass === "all" && <Check size={20} color="#6366F1" />}
            </Pressable>
            {classrooms.map((classId) => (
              <Pressable
                key={classId}
                style={styles.modalOption}
                onPress={() => {
                  setSelectedClass(classId);
                  setShowClassPicker(false);
                }}
              >
                <Text style={styles.modalOptionText}>Class {classId}</Text>
                {selectedClass === classId && <Check size={20} color="#6366F1" />}
              </Pressable>
            ))}
          </View>
        </Pressable>
      </Modal>

      {/* Children Picker Modal */}
      <Modal visible={showChildPicker} transparent animationType="slide">
        <Pressable style={styles.modalOverlay} onPress={() => setShowChildPicker(false)}>
          <View style={styles.modalContent}>
            <View style={styles.modalHeader}>
              <Text style={styles.modalTitle}>Select Children</Text>
              <Pressable
                onPress={() => {
                  if (selectedChildren.length === filteredChildren.length) {
                    setSelectedChildren([]);
                  } else {
                    setSelectedChildren(filteredChildren.map((c) => c.id));
                  }
                }}
              >
                <Text style={styles.selectAllText}>
                  {selectedChildren.length === filteredChildren.length ? "Deselect All" : "Select All"}
                </Text>
              </Pressable>
            </View>
            <ScrollView style={styles.childrenList}>
              {filteredChildren.map((child) => (
                <Pressable
                  key={child.id}
                  style={styles.modalOption}
                  onPress={() => {
                    if (selectedChildren.includes(child.id)) {
                      setSelectedChildren(selectedChildren.filter((id) => id !== child.id));
                    } else {
                      setSelectedChildren([...selectedChildren, child.id]);
                    }
                  }}
                >
                  <Text style={styles.modalOptionText}>{child.name}</Text>
                  {selectedChildren.includes(child.id) && <Check size={20} color="#6366F1" />}
                </Pressable>
              ))}
            </ScrollView>
            <Pressable
              style={styles.doneButton}
              onPress={() => setShowChildPicker(false)}
            >
              <Text style={styles.doneButtonText}>Done</Text>
            </Pressable>
          </View>
        </Pressable>
      </Modal>
    </View>
  );
}

